package com.Loops;

public class PriceOf100Items {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int total=0;
		
    for (int i=1; i<=100; i++)
    {
    	total=total+i;
    	
    }
    System.out.println("Total price of 100 items = " + total);
	}

}
