package com.NestedLoop;

public class Loop10 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		int j=1;
    for (int i=1; i<=4; i++)
    {
    	for (int a=1; a<=i; a++)
    	{
    		System.out.print(j);
    	}
    	System.out.println();
    }
	}

}
