package com.WhileLoop;

public class NumBet1To20Divis2And9break {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
     int i=1;
     while (i<=20)
     {   
     
    	 if ((i%2==0) && (i%9==0))
    	 break;
    	 System.out.println(i) ;
  
    		i++;
 
            
    	
    	
     }
   
	}

}
